import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { rentalsAPI, customersAPI, vehiclesAPI } from '../services/api';
import toast from 'react-hot-toast';
import PhotoUpload from './PhotoUpload';
import { 
  ArrowLeft, 
  ArrowRight, 
  Save, 
  User, 
  Car, 
  Calendar,
  Camera,
  CheckCircle 
} from 'lucide-react';

const NewRental = () => {
  const navigate = useNavigate();
  const [currentStep, setCurrentStep] = useState(1);
  const [loading, setLoading] = useState(false);
  
  const [customers, setCustomers] = useState([]);
  const [vehicles, setVehicles] = useState([]);
  const [categories, setCategories] = useState([]);

  const [formData, setFormData] = useState({
    // Step 1: Cliente
    customer_id: '',
    
    // Step 2: Veicolo
    vehicle_id: '',
    category_id: '',
    
    // Step 3: Date e dettagli
    pickup_date: '',
    expected_return_date: '',
    pickup_location: 'BNC Energy Rent Car',
    pickup_fuel_level: '100',
    pickup_km: '',
    pickup_damages: '',
    
    // Step 4: Tariffe
    daily_rate: '',
    total_days: 1,
    
    // Step 5: Foto (gestito separatamente)
    rental_id: null,
  });

  const [uploadedPhotos, setUploadedPhotos] = useState([]);

  useEffect(() => {
    loadInitialData();
  }, []);

  useEffect(() => {
    if (formData.pickup_date && formData.expected_return_date) {
      calculateDays();
    }
  }, [formData.pickup_date, formData.expected_return_date]);

  useEffect(() => {
    if (formData.vehicle_id) {
      const selectedVehicle = vehicles.find(v => v.id === parseInt(formData.vehicle_id));
      if (selectedVehicle) {
        setFormData(prev => ({
          ...prev,
          category_id: selectedVehicle.category_id,
          daily_rate: selectedVehicle.daily_rate,
          pickup_km: selectedVehicle.current_km,
        }));
      }
    }
  }, [formData.vehicle_id, vehicles]);

  const loadInitialData = async () => {
    try {
      const [customersRes, vehiclesRes, categoriesRes] = await Promise.all([
        customersAPI.getAll({ limit: 1000 }),
        vehiclesAPI.getAll({ available_only: 'true', limit: 1000 }),
        vehiclesAPI.getCategories(),
      ]);

      setCustomers(customersRes.data.data);
      setVehicles(vehiclesRes.data.data);
      setCategories(categoriesRes.data.data);
    } catch (error) {
      console.error('Errore caricamento dati:', error);
      toast.error('Errore nel caricamento dei dati');
    }
  };

  const calculateDays = () => {
    const start = new Date(formData.pickup_date);
    const end = new Date(formData.expected_return_date);
    const diffTime = Math.abs(end - start);
    const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24));
    setFormData(prev => ({ ...prev, total_days: diffDays || 1 }));
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const validateStep = (step) => {
    switch (step) {
      case 1:
        if (!formData.customer_id) {
          toast.error('Seleziona un cliente');
          return false;
        }
        break;
      case 2:
        if (!formData.vehicle_id) {
          toast.error('Seleziona un veicolo');
          return false;
        }
        break;
      case 3:
        if (!formData.pickup_date || !formData.expected_return_date || !formData.pickup_km) {
          toast.error('Compila tutti i campi obbligatori');
          return false;
        }
        break;
      case 4:
        if (!formData.daily_rate) {
          toast.error('Inserisci la tariffa giornaliera');
          return false;
        }
        break;
    }
    return true;
  };

  const handleNext = () => {
    if (validateStep(currentStep)) {
      if (currentStep === 4) {
        handleSubmit();
      } else {
        setCurrentStep(currentStep + 1);
      }
    }
  };

  const handlePrevious = () => {
    setCurrentStep(currentStep - 1);
  };

  const handleSubmit = async () => {
    try {
      setLoading(true);
      
      const rentalData = {
        customer_id: parseInt(formData.customer_id),
        vehicle_id: parseInt(formData.vehicle_id),
        pickup_date: formData.pickup_date,
        expected_return_date: formData.expected_return_date,
        pickup_location: formData.pickup_location,
        pickup_fuel_level: parseFloat(formData.pickup_fuel_level),
        pickup_km: parseInt(formData.pickup_km),
        pickup_damages: formData.pickup_damages,
        daily_rate: parseFloat(formData.daily_rate),
        total_days: parseInt(formData.total_days),
        extras: [],
      };

      const response = await rentalsAPI.create(rentalData);
      
      setFormData(prev => ({ ...prev, rental_id: response.data.data.rental_id }));
      setCurrentStep(5); // Vai allo step foto
      
      toast.success('Noleggio creato con successo!');
    } catch (error) {
      console.error('Errore creazione noleggio:', error);
      toast.error(error.response?.data?.message || 'Errore nella creazione del noleggio');
    } finally {
      setLoading(false);
    }
  };

  const handleFinish = () => {
    if (uploadedPhotos.length === 0) {
      toast.error('Carica almeno una foto del veicolo');
      return;
    }
    toast.success('Noleggio completato con successo!');
    navigate(`/rentals/${formData.rental_id}`);
  };

  const steps = [
    { number: 1, title: 'Cliente', icon: User },
    { number: 2, title: 'Veicolo', icon: Car },
    { number: 3, title: 'Dettagli', icon: Calendar },
    { number: 4, title: 'Tariffe', icon: CheckCircle },
    { number: 5, title: 'Foto', icon: Camera },
  ];

  const renderStepIndicator = () => (
    <div style={{
      display: 'flex',
      justifyContent: 'space-between',
      marginBottom: '32px',
      position: 'relative',
    }}>
      {steps.map((step, index) => {
        const Icon = step.icon;
        const isActive = currentStep === step.number;
        const isCompleted = currentStep > step.number;
        
        return (
          <div key={step.number} style={{ flex: 1, textAlign: 'center', position: 'relative' }}>
            {index > 0 && (
              <div style={{
                position: 'absolute',
                top: '20px',
                left: '-50%',
                right: '50%',
                height: '2px',
                background: isCompleted ? '#1a4d2e' : '#dee2e6',
              }} />
            )}
            <div style={{
              width: '40px',
              height: '40px',
              borderRadius: '50%',
              background: isActive ? '#1a4d2e' : isCompleted ? '#4a7c59' : '#dee2e6',
              color: isActive || isCompleted ? 'white' : '#6c757d',
              display: 'inline-flex',
              alignItems: 'center',
              justifyContent: 'center',
              marginBottom: '8px',
              position: 'relative',
              zIndex: 1,
            }}>
              <Icon size={20} />
            </div>
            <div style={{
              fontSize: '12px',
              fontWeight: isActive ? '600' : '400',
              color: isActive ? '#1a4d2e' : '#6c757d',
            }}>
              {step.title}
            </div>
          </div>
        );
      })}
    </div>
  );

  const renderStep1 = () => (
    <div>
      <h3 style={{ marginBottom: '24px' }}>Seleziona Cliente</h3>
      
      <div className="form-group">
        <label className="form-label required">Cliente</label>
        <select
          name="customer_id"
          className="form-control"
          value={formData.customer_id}
          onChange={handleChange}
          required
        >
          <option value="">-- Seleziona Cliente --</option>
          {customers.map(customer => (
            <option key={customer.id} value={customer.id}>
              {customer.full_name} - {customer.fiscal_code}
            </option>
          ))}
        </select>
      </div>

      {formData.customer_id && (
        <div style={{
          marginTop: '20px',
          padding: '16px',
          background: '#f8f9fa',
          borderRadius: '4px',
        }}>
          {(() => {
            const customer = customers.find(c => c.id === parseInt(formData.customer_id));
            return customer ? (
              <div>
                <strong>Dettagli Cliente:</strong>
                <div style={{ marginTop: '8px', fontSize: '14px' }}>
                  <div>📞 {customer.phone}</div>
                  <div>📧 {customer.email}</div>
                  <div>📍 {customer.address}, {customer.city}</div>
                  <div>🪪 Patente: {customer.license_number}</div>
                </div>
              </div>
            ) : null;
          })()}
        </div>
      )}
    </div>
  );

  const renderStep2 = () => (
    <div>
      <h3 style={{ marginBottom: '24px' }}>Seleziona Veicolo</h3>
      
      <div className="form-group">
        <label className="form-label required">Veicolo Disponibile</label>
        <select
          name="vehicle_id"
          className="form-control"
          value={formData.vehicle_id}
          onChange={handleChange}
          required
        >
          <option value="">-- Seleziona Veicolo --</option>
          {vehicles.map(vehicle => (
            <option key={vehicle.id} value={vehicle.id}>
              {vehicle.brand} {vehicle.model} - {vehicle.license_plate} 
              {vehicle.category_name && ` (${vehicle.category_name})`}
            </option>
          ))}
        </select>
      </div>

      {formData.vehicle_id && (
        <div style={{
          marginTop: '20px',
          padding: '16px',
          background: '#f8f9fa',
          borderRadius: '4px',
        }}>
          {(() => {
            const vehicle = vehicles.find(v => v.id === parseInt(formData.vehicle_id));
            return vehicle ? (
              <div>
                <strong>Dettagli Veicolo:</strong>
                <div style={{ marginTop: '8px', fontSize: '14px' }}>
                  <div>🚗 {vehicle.brand} {vehicle.model} ({vehicle.year})</div>
                  <div>🏷️ Targa: {vehicle.license_plate}</div>
                  <div>🎨 Colore: {vehicle.color}</div>
                  <div>⛽ Carburante: {vehicle.fuel_type}</div>
                  <div>⚙️ Cambio: {vehicle.transmission}</div>
                  <div>📊 Km attuali: {vehicle.current_km}</div>
                  {vehicle.daily_rate && (
                    <div style={{ marginTop: '8px', fontWeight: '600', color: '#1a4d2e' }}>
                      💰 Tariffa giornaliera: €{parseFloat(vehicle.daily_rate).toFixed(2)}
                    </div>
                  )}
                </div>
              </div>
            ) : null;
          })()}
        </div>
      )}
    </div>
  );

  const renderStep3 = () => (
    <div>
      <h3 style={{ marginBottom: '24px' }}>Dettagli Ritiro e Consegna</h3>
      
      <div className="form-row">
        <div className="form-group">
          <label className="form-label required">Data e Ora Ritiro</label>
          <input
            type="datetime-local"
            name="pickup_date"
            className="form-control"
            value={formData.pickup_date}
            onChange={handleChange}
            required
          />
        </div>

        <div className="form-group">
          <label className="form-label required">Data e Ora Rientro Previsto</label>
          <input
            type="datetime-local"
            name="expected_return_date"
            className="form-control"
            value={formData.expected_return_date}
            onChange={handleChange}
            required
          />
        </div>
      </div>

      <div className="form-group">
        <label className="form-label">Luogo di Ritiro</label>
        <input
          type="text"
          name="pickup_location"
          className="form-control"
          value={formData.pickup_location}
          onChange={handleChange}
        />
      </div>

      <div className="form-row">
        <div className="form-group">
          <label className="form-label required">Livello Carburante (%)</label>
          <input
            type="number"
            name="pickup_fuel_level"
            className="form-control"
            value={formData.pickup_fuel_level}
            onChange={handleChange}
            min="0"
            max="100"
            required
          />
        </div>

        <div className="form-group">
          <label className="form-label required">Km in Uscita</label>
          <input
            type="number"
            name="pickup_km"
            className="form-control"
            value={formData.pickup_km}
            onChange={handleChange}
            required
          />
        </div>
      </div>

      <div className="form-group">
        <label className="form-label">Danni Esistenti</label>
        <textarea
          name="pickup_damages"
          className="form-control"
          value={formData.pickup_damages}
          onChange={handleChange}
          rows="3"
          placeholder="Descrivi eventuali danni o graffi presenti sul veicolo..."
        />
      </div>

      {formData.total_days > 0 && (
        <div style={{
          marginTop: '20px',
          padding: '16px',
          background: '#e8f5e9',
          borderRadius: '4px',
          fontSize: '14px',
          fontWeight: '600',
          color: '#1a4d2e',
        }}>
          📅 Durata noleggio: {formData.total_days} giorn{formData.total_days === 1 ? 'o' : 'i'}
        </div>
      )}
    </div>
  );

  const renderStep4 = () => {
    const subtotal = parseFloat(formData.daily_rate || 0) * parseInt(formData.total_days || 1);
    
    return (
      <div>
        <h3 style={{ marginBottom: '24px' }}>Riepilogo Tariffe</h3>
        
        <div className="form-group">
          <label className="form-label required">Tariffa Giornaliera (€)</label>
          <input
            type="number"
            name="daily_rate"
            className="form-control"
            value={formData.daily_rate}
            onChange={handleChange}
            step="0.01"
            min="0"
            required
          />
        </div>

        <div style={{
          marginTop: '24px',
          padding: '20px',
          background: '#f8f9fa',
          borderRadius: '4px',
        }}>
          <h4 style={{ marginBottom: '16px', fontSize: '16px' }}>Riepilogo Importi</h4>
          
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
            <span>Tariffa giornaliera:</span>
            <strong>€{parseFloat(formData.daily_rate || 0).toFixed(2)}</strong>
          </div>
          
          <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '8px' }}>
            <span>Numero giorni:</span>
            <strong>{formData.total_days}</strong>
          </div>
          
          <div style={{ 
            borderTop: '2px solid #dee2e6', 
            marginTop: '12px', 
            paddingTop: '12px',
            display: 'flex', 
            justifyContent: 'space-between',
            fontSize: '18px',
            fontWeight: '700',
            color: '#1a4d2e',
          }}>
            <span>TOTALE:</span>
            <span>€{subtotal.toFixed(2)}</span>
          </div>
        </div>
      </div>
    );
  };

  const renderStep5 = () => (
    <div>
      <h3 style={{ marginBottom: '24px' }}>Foto Veicolo in Uscita</h3>
      
      <div style={{
        padding: '16px',
        background: '#fff3cd',
        border: '1px solid #ffc107',
        borderRadius: '4px',
        marginBottom: '24px',
        fontSize: '14px',
      }}>
        ⚠️ <strong>Importante:</strong> Scatta foto di tutti i lati del veicolo 
        per documentare lo stato al momento della consegna.
      </div>

      <PhotoUpload
        rentalId={formData.rental_id}
        photoType="pickup"
        onPhotosChange={setUploadedPhotos}
      />
    </div>
  );

  return (
    <div>
      <div className="top-bar">
        <div style={{ display: 'flex', alignItems: 'center', gap: '16px' }}>
          <button
            onClick={() => navigate('/rentals')}
            className="btn btn-outline-secondary"
          >
            <ArrowLeft size={18} />
          </button>
          <h1>Nuovo Noleggio</h1>
        </div>
      </div>

      <div className="content-area">
        <div className="card" style={{ maxWidth: '900px', margin: '0 auto' }}>
          <div className="card-body" style={{ padding: '32px' }}>
            {renderStepIndicator()}
            
            <div style={{ minHeight: '400px' }}>
              {currentStep === 1 && renderStep1()}
              {currentStep === 2 && renderStep2()}
              {currentStep === 3 && renderStep3()}
              {currentStep === 4 && renderStep4()}
              {currentStep === 5 && renderStep5()}
            </div>

            <div style={{
              display: 'flex',
              justifyContent: 'space-between',
              marginTop: '32px',
              paddingTop: '24px',
              borderTop: '1px solid #dee2e6',
            }}>
              {currentStep > 1 && currentStep < 5 && (
                <button
                  onClick={handlePrevious}
                  className="btn btn-secondary"
                >
                  <ArrowLeft size={18} />
                  Indietro
                </button>
              )}
              
              {currentStep < 4 && (
                <button
                  onClick={handleNext}
                  className="btn btn-primary"
                  style={{ marginLeft: 'auto' }}
                >
                  Avanti
                  <ArrowRight size={18} />
                </button>
              )}
              
              {currentStep === 4 && (
                <button
                  onClick={handleNext}
                  className="btn btn-primary"
                  disabled={loading}
                  style={{ marginLeft: 'auto' }}
                >
                  {loading ? 'Creazione...' : (
                    <>
                      <Save size={18} />
                      Crea Noleggio
                    </>
                  )}
                </button>
              )}
              
              {currentStep === 5 && (
                <button
                  onClick={handleFinish}
                  className="btn btn-success"
                  style={{ marginLeft: 'auto' }}
                >
                  <CheckCircle size={18} />
                  Completa
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewRental;
