import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { customersAPI } from '../services/api';
import toast from 'react-hot-toast';
import { UserPlus, ArrowLeft, Save } from 'lucide-react';

const NewCustomer = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  
  // Campi basati sulla tabella 'customers' di schema.sql
  const [formData, setFormData] = useState({
    full_name: '',
    fiscal_code: '',
    email: '',
    phone: '',
    address: '',
    city: '',
    zip_code: '',
    driving_license_number: '',
    id_card_number: '',
    notes: '',
    // CAMPO MANCANTE AGGIUNTO:
    is_active: true, // Corrisponde al default 'true' del DB
  });

  const handleChange = (e) => {
    const value = e.target.type === 'checkbox' ? e.target.checked : e.target.value;
    setFormData({
      ...formData,
      [e.target.name]: value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.full_name || !formData.fiscal_code || !formData.email || !formData.phone) {
      toast.error('Per favore, compila tutti i campi obbligatori.');
      return;
    }

    setLoading(true);
    try {
      await customersAPI.create(formData);
      toast.success('Cliente registrato con successo!');
      navigate('/customers');
    } catch (error) {
      console.error('Errore creazione cliente:', error);
      toast.error(error.response?.data?.message || 'Errore nella creazione del cliente');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div>
      <div className="top-bar">
        <h1>
          <UserPlus size={24} style={{ marginRight: '8px' }} />
          Nuovo Cliente
        </h1>
        <button 
          onClick={() => navigate('/customers')} 
          className="btn btn-secondary"
        >
          <ArrowLeft size={18} />
          Torna ai Clienti
        </button>
      </div>

      <div className="content-area">
        <div className="card">
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="form-row">
                {/* Dati Anagrafici */}
                <h3 className="section-title">Dati Anagrafici</h3>
                <div className="col-md-6 form-group">
                  <label className="form-label required">Nome Completo</label>
                  <input type="text" name="full_name" className="form-control" value={formData.full_name} onChange={handleChange} required />
                </div>
                <div className="col-md-6 form-group">
                  <label className="form-label required">Codice Fiscale</label>
                  <input type="text" name="fiscal_code" className="form-control" value={formData.fiscal_code} onChange={handleChange} required />
                </div>
                <div className="col-md-6 form-group">
                  <label className="form-label required">Email</label>
                  <input type="email" name="email" className="form-control" value={formData.email} onChange={handleChange} required />
                </div>
                <div className="col-md-6 form-group">
                  <label className="form-label required">Telefono</label>
                  <input type="tel" name="phone" className="form-control" value={formData.phone} onChange={handleChange} required />
                </div>

                {/* Indirizzo */}
                <h3 className="section-title">Indirizzo</h3>
                <div className="col-12 form-group">
                  <label className="form-label">Indirizzo (Via/Piazza, N.)</label>
                  <input type="text" name="address" className="form-control" value={formData.address} onChange={handleChange} />
                </div>
                <div className="col-md-6 form-group">
                  <label className="form-label">Città</label>
                  <input type="text" name="city" className="form-control" value={formData.city} onChange={handleChange} />
                </div>
                <div className="col-md-6 form-group">
                  <label className="form-label">CAP</label>
                  <input type="text" name="zip_code" className="form-control" value={formData.zip_code} onChange={handleChange} />
                </div>

                {/* Documenti */}
                <h3 className="section-title">Documenti</h3>
                <div className="col-md-6 form-group">
                  <label className="form-label">Patente di Guida N.</label>
                  <input type="text" name="driving_license_number" className="form-control" value={formData.driving_license_number} onChange={handleChange} />
                </div>
                <div className="col-md-6 form-group">
                  <label className="form-label">Carta d'Identità N.</label>
                  <input type="text" name="id_card_number" className="form-control" value={formData.id_card_number} onChange={handleChange} />
                </div>

                {/* Note e Stato */}
                <div className="col-12 form-group">
                  <label className="form-label">Note</label>
                  <textarea name="notes" className="form-control" value={formData.notes} onChange={handleChange} rows="3" />
                </div>
                
                {/* CAMPO AGGIUNTO is_active */}
                <div className="col-12 form-group" style={{ marginTop: '16px' }}>
                  <div className="form-check">
                    <input 
                      type="checkbox" 
                      id="is_active_check" 
                      name="is_active" 
                      className="form-check-input" 
                      checked={formData.is_active} 
                      onChange={handleChange} 
                    />
                    <label className="form-check-label" htmlFor="is_active_check">
                      Cliente Attivo
                    </label>
                  </div>
                </div>
                {/* FINE CAMPO AGGIUNTO */}

                <div className="col-12 mt-4">
                  <button type="submit" className="btn btn-success btn-lg" disabled={loading}>
                    {loading ? 'Salvataggio...' : (<><Save size={18} /> Salva Cliente</>)}
                  </button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default NewCustomer;
