// Login.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../contexts/AuthContext';
import toast from 'react-hot-toast';
import { LogIn } from 'lucide-react';

const Login = () => {
  const navigate = useNavigate();
  const { login } = useAuth();
  
  const [formData, setFormData] = useState({
    username: '',
    password: '',
  });
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.username || !formData.password) {
      toast.error('Inserisci username e password');
      return;
    }

    setLoading(true);
    const result = await login(formData);
    setLoading(false);

    if (result.success) {
      toast.success('Accesso effettuato con successo!');
      navigate('/');
    } else {
      toast.error(result.message);
    }
  };

  return (
    <div style={{
      minHeight: '100vh',
      display: 'flex',
      flexDirection: 'column', // Aggiunto per posizionare il footer
      alignItems: 'center',
      justifyContent: 'center',
      background: 'linear-gradient(135deg, #1a4d2e 0%, #143d24 100%)',
      padding: '20px',
    }}>
      <div style={{
        background: 'white',
        borderRadius: '8px',
        boxShadow: '0 20px 60px rgba(0, 0, 0, 0.3)',
        width: '100%',
        maxWidth: '400px',
        overflow: 'hidden',
        marginBottom: '15px', // Spazio tra form e footer
      }}>
        {/* Header */}
        <div style={{
          background: 'linear-gradient(135deg, #1a4d2e 0%, #4a7c59 100%)',
          padding: '40px 32px',
          textAlign: 'center',
          color: 'white',
        }}>
          <div style={{
            width: '80px',
            height: '80px',
            background: 'white',
            borderRadius: '16px',
            display: 'inline-flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '48px',
            fontWeight: 'bold',
            color: '#1a4d2e',
            marginBottom: '16px',
          }}>
            B
          </div>
          <h1 style={{ fontSize: '24px', fontWeight: '600', marginBottom: '8px' }}>
            BNC Energy
          </h1>
          <p style={{ fontSize: '14px', opacity: 0.9 }}>
            Sistema Gestione Noleggio
          </p>
        </div>

        {/* Form */}
        <form onSubmit={handleSubmit} style={{ padding: '32px' }}>
          <div className="form-group">
            <label className="form-label">Username</label>
            <input
              type="text"
              name="username"
              className="form-control"
              value={formData.username}
              onChange={handleChange}
              placeholder="Inserisci username"
              autoComplete="username"
              autoFocus
            />
          </div>

          <div className="form-group">
            <label className="form-label">Password</label>
            <input
              type="password"
              name="password"
              className="form-control"
              value={formData.password}
              onChange={handleChange}
              placeholder="Inserisci password"
              autoComplete="current-password"
            />
          </div>

          <button
            type="submit"
            className="btn btn-primary btn-lg"
            disabled={loading}
            style={{ width: '100%', marginTop: '8px' }}
          >
            {loading ? (
              'Accesso in corso...'
            ) : (
              <>
                <LogIn size={18} />
                Accedi
              </>
            )}
          </button>

          <div style={{
            marginTop: '24px',
            padding: '16px',
            background: '#f8f9fa',
            borderRadius: '4px',
            fontSize: '13px',
            color: '#6c757d',
          }}>
            
          </div>
        </form>
      </div>

      {/* Nuovo Footer qui per la pagina di Login */}
      <footer style={{
        textAlign: 'center',
        fontSize: '12px',
        color: 'rgba(255, 255, 255, 0.7)',
      }}>
        Sviluppato da **Antonino Giardina** - all rights reserved 2025Â®
      </footer>

    </div>
  );
};

export default Login;
